import React from 'react'
import '../App.css';
import 'react-icons/ai'
import { AiOutlineCloseCircle, AiOutlineShopping, AiOutlineShoppingCart } from 'react-icons/ai';

function DishModal({setOpenModal, selectedImg, name, price}) {
    const handleClick = (e) => {
        if (e.target.classList.contains('DishItem-backdrop')){
            setOpenModal(false);
        }
    }

    return (
        <div className='DishItem-backdrop' onClick={handleClick}>
            <div className='DishItem-backdrop-container'>

                <div style={{flexDirection: 'row', display: 'flex', backgroundColor: 'gray'}}>
                    <img src={selectedImg} />

                    <div style={{display: 'flex', justifyContent: 'space-between', flexDirection: 'column', alignItems: 'center', backgroundColor:'beige'}}>
                        <div>
                            <text>SKU</text>
                        </div>

                        <div>
                            Quantity
                        </div>

                        <div>
                            Description
                        </div>

                        <div>
                            <AiOutlineShoppingCart />
                        </div>
                    </div>
                </div>
            </div>       
        </div>
    )
}

export default DishModal
