    const menu = [
        {
          menu: 'tiramisu',
          items: [
            {
              name: 'Apple cake',
              price: 5
            },
            {
              name: 'Pineapple cake',
              price: 5
            }
          ]
        },
        {
          menu: 'seafood',
          items: [
            {
              name: 'Lobsert',
              price: 50
            },
            {
              name: 'King crab',
              price: 70
            }
          ]
        }
        
      ]
    
      const cakes = menu.map( (item) => {
        return (
          <div>
            <h1> {item.menu} </h1>
            { item.items.map(dish => <h1> {dish.name} : {dish.price} </h1>)}
          </div>
        )
      })

    const [boughtItems, setBoughtItems] = useState([]);

      items = [
        {id: 1, title: 'item #1'},
        {id: 2, title: 'item #2'},
        {id: 3, title: 'item #3'},
        {id: 4, title: 'item #4'},
        {id: 5, title: 'item #5'}
      ]

      
    return (
        <div className={Menu}>
          <Carousel>
        {items.map(item => <div key={item.id}>{item.title}</div>)}
      </Carousel>

          {/* <text> asda </text> */}
            {/* Menu side */}
            <div className='Menu-bar'>
              {/* {menu.map(menuItem => <MenuItem name={menuItem.menu}/>)} */}
            </div>

            {/* Shopping list side */}
            <div>

            </div>
        </div>
    )
}

export default Menu
