import React, { useRef, useState } from 'react'
import { Form, Card, Button, Alert, InputGroup, FormControl } from 'react-bootstrap'
import { useAuth } from '../contexts/AuthContext'
import { Link, useNavigate } from 'react-router-dom'

function LoginScreen() {
    const emailRef = useRef()
    const passwordRef = useRef()

    const { login } = useAuth()

    const [error, setError] = useState('')

    const [loading, setLoading] = useState(false)

    const navigation = useNavigate()

    const handleSubmit = async (e) => {
        e.preventDefault()


        try {
            setError('')
            setLoading(true)
            await login(emailRef.current.value, passwordRef.current.value)
            navigation('/')
        } catch (e) {
            setError('Wrong password')
        }

        setLoading(false)
    }

    return (
        <>
            <Card>
                <Card.Body>
                    <Card.Title className='text-center mb-4'>Log In</Card.Title>
                    {error && <Alert variant='danger'>{error}</Alert>}
                    <Form onSubmit={handleSubmit}>
                        <Form.Group id='email'>
                            <Form.Label  >Your email</Form.Label>
                            <Form.Control style={{borderWidth: '1'}} type='email' ref={emailRef} required/>
                        </Form.Group>

                        <Form.Group id='password'>
                            <Form.Label>Your password</Form.Label>
                            <Form.Control type='password' ref={passwordRef} required/>
                        </Form.Group>

                        <Form.Group id='checkbox'>
                            <Form.Check type='checkbox' label='Remember me' />
                        </Form.Group>

                        <InputGroup>
                            <InputGroup.Text style={{backgroundColor: 'white'}}>Icon</InputGroup.Text>
                            <FormControl id="inlineFormInputGroupUsername" placeholder="Username" />
                        </InputGroup>

                        <Button disabled={loading} className='w-100 mt-2' type='submit'>Sign Up</Button>
                    </Form>
                    
                </Card.Body>
            </Card>
            <div className='w-100 text-center mt-2'>
                Need an account? <Link to='/signup'>Sign up</Link>
            </div>
        </>
    )
}

export default LoginScreen
