  //     await updateDoc(userDoc, newFields);
  //   }

  //   const deleteUser = async (id) => {
  //     const userDoc = doc(db, "users", id);
  //     await deleteDoc(userDoc);

  //   }

    const menuRef = collection(db, 'menu');

    const [menus, setMenus] = useState([]);

    useEffect(
        () => {
            const getMenus = async () => {
                const data = await getDocs(menuRef);

                setMenus(data.docs.map(
                    menu => ({...menu.data(), id: menu.id})
                ))
            }

            getMenus();
        }
    , [])

    function showMenu(){
        return (
            <div>
            {menus.map(
                menu => {
                    return (
                        <div>
                            <h1> {menu.id}: {menu.name} </h1>
                            {menu.dishes.map(
                                dish => <h2> {dish.name}: {dish.price}</h2>
                            )}
                        </div>
                    )
                }
            )}
        </div>
        )
    }


    return (
        <div className='MenuSection-container'>
            <div className='MenuSection-menu'>
                <div className='MenuSection-menu-bar'>
                    <div className='MenuSection-menu-bar-home'>
                        <AiOutlineHome size={50} color={'react'} />
                    </div>

                    <h1> Back to home </h1>
                </div>
            </div>

            <div className='MenuSection-cart'>
            <text> MenuSection-cart </text>
            </div>
        </div>
    )
}

export default MenuSection;
