import React, { useState } from 'react'
import logo from '../pictures/logo192.png';

function MenuItem({name, price}) {
    const [isClicked, setIsClicked] = useState(false);
    // const logo = require('../pictures/logo192.png');
    const cupcake = '../pictures/cupcake.jpg';

    return (
        <div onClick={() => setIsClicked(true)}>
            <div>
                <img src={logo} alt='Logo' style={{height: 400, width: 400}}/>
            </div>

            <div>
                <img src={cupcake} alt='Logo' style={{height: 400, width: 400}}/>
            </div>
            
        </div>
    )
}

export default MenuItem;
