            if (numOfDishes - i >= 3) {
                array.push([list.at(i), list.at(i + 1), list.at(i + 2)])
                i += 3;
            } else if (numOfDishes - i >= 2) {
                array.push([list.at(i), list.at(i + 1)])
                i += 2;
