import React from 'react'
// import '../App.css';
import 'react-icons/ai'
import '../styles/DishModal.css'

import { AiFillMinusCircle, AiFillPlusCircle, AiOutlineCloseCircle, AiOutlineShopping, AiOutlineShoppingCart } from 'react-icons/ai';

function DishModal({setOpenModal, selectedImg, name, price}) {
    const handleClick = (e) => {
        if (e.target.classList.contains('DishItem-backdrop')){
            setOpenModal(false);
        }
    }

    return (
        <div className='DishItem-backdrop' onClick={handleClick}>
            <div className='DishItem-backdrop-container'>
                <div style={{
                    justifyContent: 'space-between', 
                    display: 'flex', 
                    backgroundColor: 'gray  ',
                    padding: 10
                    }}>
                        <text>ADD TO CART</text>
                        <AiOutlineCloseCircle
                        onClick={() => setOpenModal(false)}
                        size={28}
                        />
                </div>

                <div style={{
                    flexDirection: 'row', 
                    display: 'flex', 
                    backgroundColor: 'gray',
                    // padding: 10,
                    flex: 1
                    }}>
                    <div style={{padding: 10, display: 'flex'}}>
                        <img style={{}} src={selectedImg} />
                    </div>

                    <div style={{
                        display: 'flex', 
                        flexDirection: 'column', 
                        // alignItems: 'center', 
                        // backgroundColor:'beige',
                        // justifyContent: 'space-between',
                        width: '100%',
                        padding: 20
                        }}>

                        <div style={{   
                            flexDirection: 'row', 
                            display: 'flex',
                            justifyContent: 'space-between',
                        }}>
                            <div>
                                <h4>SKU</h4>
                                <h6>Quantity</h6>
                            </div>

                            <div>
                            <h4>Category</h4>
                            <h4>Name</h4>
                            </div>

                            <div>
                            <h4>Unit Price</h4>
                            <h4>Price</h4>
                            </div>
                        </div>

                        <div style={{
                            flexDirection: 'row',
                            display: 'flex',
                            justifyContent: 'space-between',
                            borderTopStyle: 'ridge',
                            borderBottomStyle: 'ridge',
                            // backgroundColor: 'gray',
                            // height: '20%',
                            padding: 10,
                            alignItems: 'center'
                            }}>
                                <text>Quantity</text>

                                <div style={{
                                    // backgroundColor: 'blue',
                                    display: 'flex',
                                    justifyContent: 'space-between',
                                }}>
                                    <AiFillMinusCircle size={32} />
                                    <text style={{marginLeft: 10, marginRight: 10}}>1</text>
                                    <AiFillPlusCircle size={32} />
                                </div>
                        </div>

                        <div style={{
                            // borderBottomStyle: 'ridge'
                        }}>
                            <text>Description</text>
                        </div>

                        <div style={{
                            display: 'flex',
                            justifyContent: 'center',
                            backgroundColor:'beige',
                            justifySelf: 'end'
                            }}>
                            <AiOutlineShoppingCart size={32}/>
                            <text>Price</text>
                        </div>
                    </div>
                </div>
            </div>       
        </div>
    )
}

export default DishModal
