import logo from './logo.svg';
import './App.css';
import Carousel from 'react-elastic-carousel';
// import styles from './App.css'
import { isValidTimestamp } from '@firebase/util';
import MenuSection from './screens/MenuSection';
import { db } from './firebase/config';
import { useEffect, useState } from 'react';
import { collection, getDoc, getDocs, doc, onSnapshot } from '@firebase/firestore';
import bg from './pictures/POS-background.png';
import { FaCopy } from 'react-icons/fa';
import { DataProvider } from './contexts/DataContext';
import { UserProvider } from './contexts/UserContext';
import { PaymentProvider } from './contexts/PaymentContext';
import { MenuProvider } from './contexts/MenuContext';
import { Route, Router, Routes } from 'react-router';
import { BrowserRouter, Link } from 'react-router-dom';
import 'react-icons/gr';
import { GrHome, GrHomeRounded } from 'react-icons/gr';
import Nav from 'react-bootstrap/Nav';
import 'bootstrap/dist/css/bootstrap.min.css';
function Home() {
  return <h2>Home</h2>;
}

function About() {
  return <h2>About</h2>;
}

function Users() {
  return <h2>Users</h2>;
}

function Test() {
  return (
<div style={{flexDirection: 'row', display: 'flex'}}>
<BrowserRouter>
      <Nav
        // variant='pills'
        justify
        className='flex-column'
        style={{background: 'gray', width: '10%', height: '100vh'}}
        // defaultActiveKey='/about'
        // activeKey='/home'
      >
        <Nav.Item style={{backgroundColor: 'beige'}}>
          <Nav.Link href='/'>
            <div style={{}}>
            <GrHomeRounded size={36} style={{backgroundColor: 'yellow'}} />
            </div>
          </Nav.Link>
        </Nav.Item>

        <Nav.Item>
          <Nav.Link href='/about'>About</Nav.Link>
        </Nav.Item>
      </Nav>

      <Routes>
        <Route path='/' element={<Home/>} />
        <Route path='/about' element={<About/>} />
      </Routes>
    </BrowserRouter>
</div>
  )
}

function RunApp() {
  return (
    <div style={{backgroundImage: `url(${bg})`, height: '50%', backgroundSize: '100%'}}>
    <DataProvider>
      <UserProvider>
        <MenuProvider>
          <PaymentProvider>
            <MenuSection />
          </PaymentProvider>
        </MenuProvider>
      </UserProvider>
    </DataProvider>
    </div>
  )
}

function App() {

  return (
    // <RunApp />
    <Test/>
  );
}

export default App;
