import React, { useEffect } from 'react'
import { db } from '../fire base/config'
import 'firebase/firestore'

function ListUsers() {
    const customersCol = "customers";

    const [customers, setCustomers] = useState([]);

    const customerColRef = getDocs(db, customersCol);

    useEffect(() => {
        const getCustomers = async () => {
            const data = await getDocs(customerColRef);
            
            setCustomers(data.docs.map( customer => ({...customer.data(), id: customer.id})))
        }

        getCustomers();
    }, [])

    return (
        <div>
            {
                customers.map((customer) => {
                    <div>
                        <h1>{customer.name}</h1>
                        <h1>{customer.sex}</h1>
                        <h1>{customer.phoneNumber}</h1>
                    </div>
                })
            }
        </div>
    )
}

export default ListUsers
