import React from 'react'
// import '../App.css';
import 'react-icons/ai'
import '../styles/DishModal.css'

import { AiFillMinusCircle, AiFillPlusCircle, AiOutlineCloseCircle, AiOutlineShopping, AiOutlineShoppingCart } from 'react-icons/ai';

function DishModal({setOpenModal, selectedImg, name, price}) {
    const handleClick = (e) => {
        if (e.target.classList.contains('DishItem-backdrop')){
            setOpenModal(false);
        }
    }

    return (
        <div className='DishItem-backdrop' onClick={handleClick}>
            <div className='DishItem-backdrop-container'>
                <div style={{
                    justifyContent: 'space-between', 
                    display: 'flex', 
                    backgroundColor: 'cyan',
                    padding: 10
                    }}>
                        <text>ADD TO CART</text>
                        <AiOutlineCloseCircle
                        onPress={() => {}}
                        size={28}
                        />
                </div>

                <div style={{
                    flexDirection: 'row', 
                    display: 'flex', 
                    backgroundColor: 'gray',
                    }}>
                    <img src={selectedImg} />

                    <div style={{
                        display: 'flex', 
                        flexDirection: 'column', 
                        // alignItems: 'center', 
                        backgroundColor:'beige',
                        justifyContent: 'space-between',
                        width: '100%',
                        }}>

                        <div style={{
                            flexDirection: 'row', 
                            display: 'flex',
                            // justifyContent: 'space-around'
                        }}>
                            <div>
                                <h4>SKU</h4>
                                <h6>Quantity</h6>
                            </div>

                            <div>
                            <h4>Category</h4>
                            <h4>Name</h4>
                            </div>

                            <div>
                            <h4>Unit Price</h4>
                            <h4>Price</h4>
                            </div>
                        </div>

                        <div style={{flexDirection: 'row', display: 'flex'}}>
                            <text>Quantity</text>

                            <div>
                                <AiFillMinusCircle />
                                <text>1</text>
                                <AiFillPlusCircle />
                            </div>
                        </div>

                        <div>
                            <text>Description</text>
                        </div>

                        <div>
                            <AiOutlineShoppingCart />
                        </div>
                    </div>
                </div>
            </div>       
        </div>
    )
}

export default DishModal
