        //     <img src={picture} style={{width: 100, height: 100, borderRadius: 20}}/>
        //     {/* <text> {id} </text> */}
        //     <text style={{fontWeight: 'bold', fontSize: 18, marginTop: 5}}> {name} </text>
        // </div>

            <Card className='d-flex justify-content-center align-items-center' style={{width: 200}}>
                <Card.Img variant='top' src={picture} style={{width: 100, height: 100}}/>
                <Card.Body>
                    <Card.Title>{name}</Card.Title>
                </Card.Body>
            </Card>
    )
}

export default MenuItem
