import React, { useState } from 'react'
import '../App.css'
function Menu() {
    const menu = [
        {
          menu: 'tiramisu',
          items: [
            {
              name: 'Apple cake',
              price: 5
            },
            {
              name: 'Pineapple cake',
              price: 5
            }
          ]
        },
        {
          menu: 'seafood',
          items: [
            {
              name: 'Lobsert',
              price: 50
            },
            {
              name: 'King crab',
              price: 70
            }
          ]
        }
        
      ]
    
      const cakes = menu.map( (item) => {
        return (
          <div>
            <h1> {item.menu} </h1>
            { item.items.map(dish => <h1> {dish.name} : {dish.price} </h1>)}
          </div>
        )
      })

    const [boughtItems, setBoughtItems] = useState([]);
      
    return (
        <div>
            {/* Menu side */}
            <div className='Menu-bar'>

            </div>

            {/* Shopping list side */}
            <div>

            </div>
        </div>
    )
}

export default Menu
