import React, { Component } from "react";
import Slider from "react-slick";

export default class SimpleSlider extends Component {


  render() {
    const items = [1,2,3,4,5,,6,7,8,9];

    const settings = {
      dots: true,
      infinite: true,
      speed: 500,
      slidesToShow: 3,
      slidesToScroll: 3
    };
    return (
      <div>
        <h2> Single Item</h2>
        <Slider {...settings}>
          {items.map( (item) => <div style={{justifyContent:'center', backgroundColor: 'beige'}}> {item} </div>)}
        </Slider>
      </div>
    );
  }
}
