    const [isClicked, setIsClicked] = useState(false);
    // const logo = require('../pictures/logo192.png');
    // const cupcake = require('../pictures/cupcake.jpg');


    return (
        <div 
        sytle={{broderWidth: 2}}
        onClick={() => setIsClicked(!isClicked)}>
            <div>
                <img src={cupcake} alt='Cupcake' style={{height: 200, width: 200}}/>
                <h1 style={styles.bigblue}> Cupcake </h1>
            </div>
            
        </div>
    )
}

export default MenuItem;
