import logo from './logo.svg';
import './App.css';
import Carousel from 'react-elastic-carousel';
// import styles from './App.css'
import Item from './comps/Item';
import { isValidTimestamp } from '@firebase/util';
import MenuSection from './screens/MenuSection';
import { db } from './firebase/config';
import { useEffect, useState } from 'react';
import { collection, getDoc, getDocs } from '@firebase/firestore';
import {data} from './comps/Data';

function App() {
  const [menus, setMenus] = useState([]);
  const [selectedMenuId, setSelectedMenuId] = useState('1');
  const [refreshCount, setRefreshCount] = useState(0);

  const handle = () => {
    setSelectedMenuId(data().at(0).id);
  }

  useEffect(
    () => {
      const getMenus = async () => {
        const menusRef = collection(db, 'menu');
        const data = await getDocs(menusRef);

        setMenus(data.docs.map(
          item => ({...item.data(), id: item.id})
        ))
      }


      const listMenu = data();
      setMenus(listMenu);
      // setSelectedMenuId(listMenu.at(0).id);
      handle();
      // console.log("The first menu appear at id:", selectedMenuId);
    }
  , [])

  const menusRef = collection(db, 'menus');
  const [menusList, setMenusList] = useState([]);

  useEffect(
    () => {
      const getMenus = async () => {
        const data = await getDocs(menusRef);

          data.docs.forEach(
            menu => {

              const getDishes = async () => {
                var dishesRef = collection(db, 'menus' + '/' + menu.id + '/' + 'dishes');
                const dishes = await getDocs(dishesRef);

                console.log('Adding at menu id', menu.id);
                const dishesList = dishes.docs.map( dish => ({...dish.data(), id: dish.id}));

                setMenusList(
                  (prevList) => ([...prevList, {...menu.data(), id: menu.id, dishes: dishesList}])
                )
              }

              getDishes();
            }
          )
      }

      getMenus();
    }
  , [])

  return (
    // <MenuSection 
    //   menus={menus}
    //   selectedMenuId={selectedMenuId}
    //   setSelectedMenuId={setSelectedMenuId}

    // />

    <div>
      {

      menusList.map(
        menu => {
          console.log(menusList);
          console.log('The menu with id', menu.id, ':', menu);

          return (
              <div>
              <text> {menu.id}: {menu.name} </text>
              {
                menu.dishes.map(
                  dish => (<h1> {dish.id}: {dish.name}, {dish.price} </h1>)
                )
              }
            </div>
          )
        }
      )}
    </div>
  );
}

export default App;
