import React from 'react'
import {useState} from 'react'

function CounterHook() {
    [count, setCount] = useState(0)


    return (
        <div>
            <h1>This is a counter</h1> 
            <button onClick = {() => setCount(count + 1)}> {count} times </button>        
        </div>
    )
}

export default CounterHook
