    const customersColRef = collection(db, "customers")
    const [customers, setCustomers] = useState([]);

    const [newName, setNewName] = useState("");
    const [newSex, setNewSex] = useState("");
    const [ewPhoneNumber, setNewPhoneNumber] = useState("");

    const addCustomer = async () => {
        await addDoc(customersColRef, {name: newName, sex: newSex, phoneNumber:setNewPhoneNumber})
    }

    return (
        <div>
            <table> 
                <ul>
                    <input placeholder="Name..." onChange={(e) => setNewName(e.target.value)}></input>
                </ul>

                <ul> <input placeholder="Sex..." onChange={e => setNewSex(e.target.value)}></input> </ul>

                <ul> <input placeholder="Phone number..." onChange={(e) => setNewName(e.target.value)}></input> </ul>

                <ul> <button onClick={() => addCustomer}>Insert customer</button> </ul>
            </table>
            
        </div>
    )
}



export default InputUser
