import React, {useState} from 'react'
import '../App.css'
import cupcake from '../pictures/cupcake.jpg';
import 'react-icons/ai';
import { AiOutlineShoppingCart } from 'react-icons/ai';

function DishItem({key, name, price}) {
    const [isClicked, setIsClicked] = useState(false);

    return (
        <div
            className='dishItem-container'
        >
            <img src={cupcake} className='dishItem-image' />
            <text className='dishItem-name'> {key}.{name}</text>

            <div className='dishItem-price-container'>
                <text style={{fontSize: 20}}>{price}</text>
                <AiOutlineShoppingCart 
                    style={{backgroundColor: 'pink', alignItems:'end'}}
                    size={28}
                />

            </div>
        </div>
    )
}

export default DishItem
